/* eslint-disable */
import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { gettext, siteRoot } from '../../utils/constants';
import EmptyTip from '../../components/empty-tip';
import LinkItem from '../../components/share-link-panel/link-item';
import CommonOperationConfirmationDialog from '../../components/dialog/common-operation-confirmation-dialog';
import Loading from '../../components/loading';
import { EvalProFunc, EvalQuotaShareLinks } from '../ad';

const propTypes = {
    shareLinks: PropTypes.array.isRequired,
    permissionOptions: PropTypes.array.isRequired,
    setMode: PropTypes.func.isRequired,
    showLinkDetails: PropTypes.func.isRequired,
    toggleSelectAllLinks: PropTypes.func.isRequired,
    toggleSelectLink: PropTypes.func.isRequired,
    deleteLink: PropTypes.func.isRequired,
    deleteShareLinks: PropTypes.func.isRequired,
    isLoadingMore: PropTypes.bool,
    handleScroll: PropTypes.func,
    hideBatchButton: PropTypes.bool
};

class LinkList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isDeleteShareLinksDialogOpen: false
        };
    }

    toggleDeleteShareLinksDialog = () => {
        this.setState({ isDeleteShareLinksDialogOpen: !this.state.isDeleteShareLinksDialogOpen });
    };

    toggleSelectAllLinks = (e) => {
        this.props.toggleSelectAllLinks(e.target.checked);
    };

    cancelSelectAllLinks = () => {
        this.props.toggleSelectAllLinks(false);
    };

    exportSelectedLinks = () => {
        const { shareLinks } = this.props;
        const selectedLinks = shareLinks.filter(item => item.isSelected);
        let url = `${siteRoot}share/link/export-excel/?`;
        url += selectedLinks.map(item => `token=${item.token}`).join('&');
        location.href = url;
    };

    render() {
        const { shareLinks, permissionOptions, isLoadingMore, handleScroll, hideBatchButton } = this.props;
        const selectedLinks = shareLinks.filter(item => item.isSelected);
        const isAllLinksSelected = shareLinks.length == selectedLinks.length;

        return (
            <Fragment>
                <div className="d-flex justify-content-between align-items-center pb-2 mt-1 pr-1 border-bottom">
                    <h6 className="font-weight-normal m-0">{gettext('Share Link')}</h6>
                    <div className="d-flex">
                        {selectedLinks.length == 0 ? (
                            <>
                                <button className="btn btn-sm btn-outline-primary mr-2" onClick={EvalQuotaShareLinks(this.props.setMode.bind(this, 'singleLinkCreation'))}>{gettext('Generate Link')}</button>
                                {!hideBatchButton && (
                                    <button className="btn btn-sm btn-outline-primary" onClick={EvalProFunc(this.props.setMode.bind(this, 'linksCreation'))}>{gettext('Generate links in batch')}</button>
                                )}
                            </>
                        ) : (
                            <>
                                <button className="btn btn-sm btn-secondary mr-2" onClick={this.cancelSelectAllLinks}>{gettext('Cancel')}</button>
                                <button className="btn btn-sm btn-secondary mr-2" onClick={this.toggleDeleteShareLinksDialog}>{gettext('Delete')}</button>
                                <button className="btn btn-sm btn-secondary" onClick={this.exportSelectedLinks}>{gettext('Export')}</button>
                            </>
                        )}
                    </div>
                </div>
                {shareLinks.length == 0 ? (
                    <EmptyTip forDialog={true}>
                        <p className="text-secondary">{gettext('No password-protected share links yet')}</p>
                        <p className="text-secondary small">{gettext('Add a password to any share link to make it accessible in China (no VPN needed)')}</p>
                    </EmptyTip>
                ) : (
                    <div className='share-list-container share-link'>
                        {shareLinks.length > 0 && (
                            <div style={{
                                fontSize: '12px',
                                color: '#666',
                                padding: '8px 12px',
                                background: '#f8f9fa',
                                borderRadius: '4px',
                                marginBottom: '12px',
                                marginTop: '8px'
                            }}>
                                ‚ÑπÔ∏è {gettext('These links have passwords and work worldwide, including China (no VPN needed).')}
                                <br />
                                üí° {gettext('Click on a link to view its details and copy link & password')}
                            </div>
                        )}
                        <table className="table-place-header">
                            <thead>
                                <tr>
                                    <th width="5%" className="text-center">
                                        <input type="checkbox" checked={isAllLinksSelected} className="vam" onChange={this.toggleSelectAllLinks} />
                                    </th>
                                    <th width="26%">{gettext('Link')}</th>
                                    <th width="30%">{gettext('Permission')}</th>
                                    <th width="25%">{gettext('Expiration')}</th>
                                    <th width="14%"></th>
                                </tr>
                            </thead>
                        </table>
                        <div className='table-real-container' onScroll={handleScroll}>
                            <table className="table-real-content table-thead-hidden">
                                <thead>
                                    <tr>
                                        <th width="5%" className="text-center"></th>
                                        <th width="26%">{gettext('Link')}</th>
                                        <th width="30%">{gettext('Permission')}</th>
                                        <th width="25%">{gettext('Expiration')}</th>
                                        <th width="14%"></th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {shareLinks.map((item, index) => {
                                        return (
                                            <LinkItem
                                                key={index}
                                                item={item}
                                                permissionOptions={permissionOptions}
                                                showLinkDetails={this.props.showLinkDetails}
                                                toggleSelectLink={this.props.toggleSelectLink}
                                                deleteLink={this.props.deleteLink}
                                            />
                                        );
                                    })}
                                </tbody>
                            </table>
                            {isLoadingMore && <Loading />}
                        </div>
                    </div>
                )}
                {this.state.isDeleteShareLinksDialogOpen && (
                    <CommonOperationConfirmationDialog
                        title={gettext('Delete share links')}
                        message={gettext('Are you sure you want to delete the selected share links?')}
                        executeOperation={this.props.deleteShareLinks}
                        confirmBtnText={gettext('Delete')}
                        toggleDialog={this.toggleDeleteShareLinksDialog}
                    />
                )}
            </Fragment>
        );
    }
}

LinkList.propTypes = propTypes;

export default LinkList;
