    isColor: true,
    recentUsedColorsKey: RECENT_USED_HIGHLIGHT_COLORS_KEY,
    defaultLastUsedColor: DEFAULT_LAST_USED_HIGHLIGHT_COLOR
  }, {
    id: "sdoc-font-".concat(COLOR),
    iconClass: 'sdocfont sdoc-font-color',
